import './App.css';
import Leaderboard from './data/leaderboard.json';

// console.log(Object.keys(sortDescending));
// console.log(Object.values(Leaderboard));

const App = () => {

  // Sort converts number into string so instead we execute compareFunction. Thereby, comparing return value of the compare function instead, which in our case is typeOf Number.
  // If a > b sort a after b
  // If b > a sort b after a
  // If a = b do not change order
  // return b-a to sort in descending order
  // here we sort by numbers of bananas
  const arrLeaderBoard = Object.values(Leaderboard);
  const sortDescending = arrLeaderBoard.sort((a, b) => {
    return b.bananas - a.bananas;
  });

  let rank = 1;
  for (var i = 0; i < sortDescending.length; i++) {
  // increase rank only if current score less than previous
  if (i > 0 && sortDescending[i].bananas < sortDescending[i - 1].bananas) {
    rank++;
  }
    sortDescending[i].rank = rank;
  }

  console.log(sortDescending);

  return (
    <div className="container">
      <div className="input-group mb-3 mt-5">
        <input type="text" className="form-control" placeholder="Insert User ID"/>
        <button className="btn btn-outline-primary" type="button">Search</button>
      </div>

      <table className="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Rank</th>
            <th scope="col">Number Of Bananas</th>
            <th scope="col">isCurrentUser?</th>
          </tr>
        </thead>
        <tbody>
          {sortDescending.map((data ) => {
            // console.log(data.name);
            return (
              <tr key={data.uid}>
                <td>{data.name}</td>
                <td>{data.rank}</td>
                <td>{data.bananas}</td>
                <td>Rank</td>
              </tr>
            );
          })}

        </tbody>


      </table>
    </div>
  );
}

export default App;
